import React, { Component } from "react";
import { Link } from "react-router-dom";
// import "./style.css"

class Nav extends Component {
  state = {
    open: false,
    width: window.innerWidth
  };

  updateWidth = () => {
    const newState = { width: window.innerWidth };

    if (this.state.open && newState.width > 991) {
      newState.open = false;
    }

    this.setState(newState);
  };

  toggleNav = () => {
    this.setState({ open: !this.state.open });
  };

  componentDidMount() {
    window.addEventListener("resize", this.updateWidth);
  }

  componentWillMount() {
    window.removeEventListener("resize", this.updateWidth);
  }



  render() {
    return (
      <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
        <Link className="navbar-brand" href="/">
          React Reading List
      </Link>
        <button onClick={this.toggleNav} className="navbar-toggler" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle Navigation">
          <span className="navbar-toggler-icon" />
        </button>
        <div className={`${this.state.open ? "" : "collapse"}navbar-collapse`} id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link onClick={this.toggleNav} className={window.location.pathname == "/saved" ? "nav-link active" : "nav-link"} to="/saved">
                Saved Books
              </Link>

            </li>

          </ul>
        </div>
      </nav>
    );
  }
}

export default Nav;
